using System;using System.Collections.Generic;using System.Linq;using System.Text;using UnityEngine;namespace SeriousGameToolbox.I3D.Imports{    public class Import    {        public static string ImportTextAsset(string path)        {            UnityEngine.Object resource = Resources.Load(path);            if (resource != null)            {                if (resource is TextAsset)                {                    return ((TextAsset)resource).text;                }                else                {                    throw new InvalidCastException("The resource at path " + path + " is not of type TextAsset.");                }            }            else            {                throw new NullReferenceException("The resource at path " + path + " could not be found");            }        }        public static GameObject ImportGameObject(string path)        {            UnityEngine.Object resource = Resources.Load(path);            if (resource != null)            {                if (resource is GameObject)                {                    return (GameObject)resource;                }                else                {                    throw new InvalidCastException("The resource at path " + path + " is not of type GameObject.");                }            }            else            {                throw new NullReferenceException("The resource at path " + path + " could not be found");            }        }    }}